import sys

def main():
    input = sys.stdin.read
    data = input().split()
    t = int(data[0])
    index = 1
    results = []
    for _ in range(t):
        n = int(data[index])
        m = int(data[index+1])
        k = int(data[index+2])
        index += 3

        if k == 1:
            if n == 1 and m == 0:
                results.append("YES")
            else:
                results.append("NO")
        else:
            if n == 1:
                results.append("YES" if m == 0 else "NO")
            else:
                min_edges = n - 1
                max_edges = n * (n - 1) // 2
                if m >= min_edges and m <= max_edges:
                    results.append("YES")
                else:
                    results.append("NO")

    print("\n".join(results))

if __name__ == "__main__":
    main()
