def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    t = int(data[0])
    index = 1
    results = []

    for _ in range(t):
        a = int(data[index])
        b = int(data[index+1])
        c = int(data[index+2])
        index += 3

        total = a + 2 * b + 3 * c
        if total % 2 == 0:
            results.append(0)
        else:
            # Adjust based on modulo with 3
            mod = total % 3
            if mod == 1:
                results.append(1)
            else:
                results.append(2)

    for res in results:
        print(res)

if __name__ == "__main__":
    main()
