    data = input().split()

    n = int(data[0])
    m = int(data[1])
    strings = data[2:2 + n]

    def custom_key(s):
        key = []
        for i in range(m):
            if (i + 1) % 2 == 1:  # odd index (1-based)
                key.append(s[i])
            else:  # even index (1-based)
                key.append(chr(255 - ord(s[i])))  # invert the character
        return tuple(key)

    indexed_strings = [(i + 1, s) for i, s in enumerate(strings)]
    indexed_strings.sort(key=lambda x: custom_key(x[1]))

    result = [str(idx) for idx, s in indexed_strings]
    print(' '.join(result))

