        m = int(data[idx+1])
        k = int(data[idx+2])
        idx += 3

        grid = []
        for i in range(n):
            grid.append(data[idx])
            idx += 1

        # Check if the field can be formed by ticks of size >= k
        # We will use dynamic programming to track the possible positions of ticks

        # dp[i][j] = True if cell (i,j) can be part of a tick
        dp = [[False] * m for _ in range(n)]

        # First, mark all '*' cells as possible start of a tick
        for i in range(n):
            for j in range(m):
                if grid[i][j] == '*':
                    dp[i][j] = True

        # Now, for each cell, check if it can be part of a tick
        # We will check all possible diagonals
        for i in range(n):
            for j in range(m):
                if grid[i][j] == '*':
                    # Check top-left diagonal
                    for d in range(1, min(i, j) + 1):
                        if d >= k:
                            # Check if all cells from (i-d, j-d) to (i, j) are '*'
                            valid = True
                            for h in range(d + 1):
                                if grid[i - h][j - h] != '*':
                                    valid = False
                                    break
                            if valid:
                                # Mark all these cells as part of a tick
                                for h in range(d + 1):
                                    dp[i - h][j - h] = True
                                    if h > 0:
                                        dp[i - h][j - h] = True
                    # Check top-right diagonal
                    for d in range(1, min(i, m - j - 1) + 1):
                        if d >= k:
                            # Check if all cells from (i-d, j+d) to (i, j) are '*'
                            valid = True
                            for h in range(d + 1):
                                if grid[i - h][j + h] != '*':
                                    valid = False
                                    break
                            if valid:
                                # Mark all these cells as part of a tick
                                for h in range(d + 1):
                                    dp[i - h][j + h] = True
                                    if h > 0:
                                        dp[i - h][j + h] = True

        # Now, check if all '*' cells are marked as part of a tick
