        for i in range(n):
            for j in range(m):
                if grid[i][j] == '*' and not dp[i][j]:
                    valid = False
                    break
            if not valid:
                break

        if valid:
            results.append("YES")
        else:
            results.append("NO")

    print("\n".join(results))

