def can_erase(s):
    count = {'A': 0, 'B': 0, 'C': 0}
    for c in s:
        count[c] += 1

    # We need to have equal number of A and B, and equal number of B and C
    # Because each operation removes one A and one B or one B and one C
    # So, the number of A must equal the number of B, and the number of B must equal the number of C
    if count['A'] == count['B'] and count['B'] == count['C']:
        return "YES"
    else:
        return "NO"

def main():
    t = int(input())
    for _ in range(t):
        s = input().strip()
        print(can_erase(s))

main()

