from collections import deque
import sys

def main():
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    x = list(map(int, data[1:]))
    x.sort()

    dq = deque(x)

    is_vova_turn = True

    while len(dq) > 2:
        if is_vova_turn:
            if dq[-1] - dq[1] > dq[-2] - dq[0]:
                dq.pop()
            else:
                dq.popleft()
        else:
            if dq[-1] - dq[1] < dq[-2] - dq[0]:
                dq.pop()
            else:
                dq.popleft()
        is_vova_turn = not is_vova_turn

    print(dq[1] - dq[0])

if __name__ == "__main__":
    main()
