def alphabet_war(fight):
    # Define the power values for each side
    left_power = {'w': 4, 'p': 3, 'b': 2, 's': 1}
    right_power = {'m': 4, 'q': 3, 'd': 2, 'z': 1}

    # Convert string to list for easier manipulation
    chars = list(fight)
    n = len(chars)

    # Create array to mark destroyed positions
    destroyed = [False] * n

    # Mark positions destroyed by bombs
    for i in range(n):
        if chars[i] == '*':
            # Destroy left adjacent character
            if i > 0:
                destroyed[i-1] = True
            # Destroy right adjacent character
            if i < n-1:
                destroyed[i+1] = True

    # Calculate scores for each side
    left_score = 0
    right_score = 0

    for i in range(n):
        if not destroyed[i] and chars[i] != '*':
            if chars[i] in left_power:
                left_score += left_power[chars[i]]
            elif chars[i] in right_power:
                right_score += right_power[chars[i]]

    # Determine winner
    if left_score > right_score:
        return "Left side wins!"
    elif right_score > left_score:
        return "Right side wins!"
    else:
        return "Let's fight again!"

# Read input and call function
fight = input().strip()
result = alphabet_war(fight)
print(result)

def specialNumber(n):
    # Convert the number to a string to check each digit
    for digit in str(n):
        # If any digit is outside the range 0-5, it's not special
        if int(digit) > 5:
            return "NOT!!"
    return "Special!!"

# Read input and process
n = int(input())
print(specialNumber(n))
