def vector_affinity(vec1, vec2):
    # Check if vectors are of the same length
    if len(vec1) != len(vec2):
        return 0.0

    # Count matching elements at the same index
    match_count = sum(1 for a, b in zip(vec1, vec2) if a == b)

    # Calculate affinity as a fraction of matching elements
    return match_count / len(vec1)

# Read input from standard input
def main():
    vec1 = list(map(int, input().strip('[]').split(',')))
    vec2 = list(map(int, input().strip('[]').split(',')))
    result = vector_affinity(vec1, vec2)
    print(f"{result:.1f}")

if __name__ == "__main__":
    main()
