def digit_sum(x):
    return sum(int(d) for d in str(x))

def solve(n):
    max_sum = 0
    for a in range(n + 1):
        b = n - a
        current_sum = digit_sum(a) + digit_sum(b)
        if current_sum > max_sum:
            max_sum = current_sum
    return max_sum

if __name__ == "__main__":
    n = int(input())
    print(solve(n))

