import sys
from collections import Counter

def mix(s1, s2):
    # Count lowercase letters only
    count1 = Counter(c for c in s1 if c.islower())
    count2 = Counter(c for c in s2 if c.islower())

    # Get all unique lowercase letters from both strings
    all_letters = set(count1.keys()).union(set(count2.keys()))

    result = []

    for letter in all_letters:
        max_count = max(count1.get(letter, 0), count2.get(letter, 0))
        if max_count > 1:
            if count1.get(letter, 0) == count2.get(letter, 0):
                prefix = "=:"
            elif count1.get(letter, 0) > count2.get(letter, 0):
                prefix = "1:"
            else:
                prefix = "2:"
            result.append(prefix + letter * max_count)

    # Sort by length descending, then lexicographically
    result.sort(key=lambda x: (-len(x), x))

    return '/'.join(result)

def main():
    s1 = sys.stdin.readline().strip()
    s2 = sys.stdin.readline().strip()
    print(mix(s1, s2))

if __name__ == "__main__":
    main()
