def green(n):
    def is_green(num):
        return str(num * num).endswith(str(num))

    count = 0
    num = 1
    while True:
        if is_green(num):
            count += 1
            if count == n:
                return str(num)
        num += 1

# Read input from stdin
import sys

if __name__ == "__main__":
    n = int(sys.stdin.read().strip())
    print(green(n))
