def same_structure_as(a, b):
    if isinstance(a, list) and isinstance(b, list):
        if len(a) != len(b):
            return False
        for x, y in zip(a, b):
            if not same_structure_as(x, y):
                return False
        return True
    elif not isinstance(a, list) and not isinstance(b, list):
        return True
    else:
        return False

