import sys
import threading
from collections import defaultdict

def main():
    import sys
    sys.setrecursionlimit(1 << 25)
    input = sys.stdin.readline

    t = int(input())
    for _ in range(t):
        n, m = map(int, input().split())
        graph = defaultdict(list)
        for _ in range(m):
            i, j, c = input().split()
            i = int(i)
            j = int(j)
            graph[i].append((j, c))
            graph[j].append((i, 'imposter' if c == 'crewmate' else 'crewmate'))

        # We try to assign roles and check for contradictions
        # We use a dictionary to store the role of each player
        # 0 = unassigned, 1 = crewmate, 2 = imposter

        role = [0] * (n + 1)
        valid = True
        result = 0

        def dfs(u, r):
            nonlocal valid
            if role[u] != 0:
                if role[u] != r:
                    valid = False
                return
            role[u] = r
            for v, c in graph[u]:
                expected = 1 if c == 'crewmate' else 2
                if r == 1:  # crewmate
                    if expected != role[v]:
                        if role[v] == 0:
                            dfs(v, expected)
                        else:
                            if role[v] != expected:
                                valid = False
                else:  # imposter
                    if expected != role[v]:
                        if role[v] == 0:
                            dfs(v, expected)
                        else:
                            if role[v] != expected:
                                valid = False

        # Try to assign as many imposters as possible
        # We try to assign 1 as crewmate and 2 as imposter
        for i in range(1, n + 1):
            if role[i] == 0:
                dfs(i, 2)  # try imposter first
                if not valid:
                    break

        if not valid:
            print(-1)
        else:
            # Count the number of imposters
            result = sum(1 for i in range(1, n + 1) if role[i] == 2)
            print(result)

threading.Thread(target=main).start()
