import sys

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    a = list(map(int, data[1:n+1]))
    b = data[n+1]

    count = 0
    stack = []

    for i in range(n):
        current = a[i]
        if b[i] == '*':
            stack.append(current)
        else:  # b[i] == '/'
            if not stack:
                stack.append(current)
            else:
                if stack[-1] % current == 0:
                    stack.append(stack[-1] // current)
                else:
                    stack = [current]

        count += len(stack)

    print(count)

if __name__ == "__main__":
    main()
