import sys

def solve() -> None:
    data = sys.stdin.buffer.read().split()
    if not data:
        return
    n = int(data[0])
    a = list(map(int, data[1:1 + n]))

    # maximal possible OR value is smaller than 2^20
    max_bit = max(a).bit_length()          # 0 for the array of only zeros
    size = 1 << max_bit                     # 2^max_bit
    seen = bytearray(size)                  # seen[v] == 1  <=>  value v appeared
    distinct = 0

    prev = set()                            # Pi‑1

    for x in a:
        cur = {x}
        for v in prev:
            cur.add(v | x)

        for val in cur:
            if seen[val] == 0:
                seen[val] = 1
                distinct += 1

        prev = cur

    print(distinct)


if __name__ == "__main__":
    solve()

