def is_composite(n):
    if n < 4:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return True
    return False

t = int(input())
for _ in range(t):
    n = int(input())
    a = list(map(int, input().split()))

    total_sum = sum(a)

    if is_composite(total_sum):
        print(n)
        print(' '.join(map(str, range(1, n + 1))))
    else:
        # Try removing each element to see if we get a composite sum
        found = False
        for i in range(n):
            if is_composite(total_sum - a[i]):
                print(n - 1)
                result = []
                for j in range(n):
                    if j != i:
                        result.append(j + 1)
                print(' '.join(map(str, result)))
                found = True
                break

        # If removing one element doesn't work, try removing two elements
        if not found:
            for i in range(n):
                for j in range(i + 1, n):
                    if is_composite(total_sum - a[i] - a[j]):
                        print(n - 2)
                        result = []
                        for k in range(n):
                            if k != i and k != j:
                                result.append(k + 1)
                        print(' '.join(map(str, result)))
                        found = True
                        break
                if found:
                    break

