import sys
import threading

def main():
    sys.setrecursionlimit(1 << 25)

    n = int(sys.stdin.readline())
    A = list(map(int, sys.stdin.readline().split()))

    memo = {}

    def can_current_player_win(l, r, last):
        if l > r:
            return False  # No moves left, current player loses

        if (l, r, last) in memo:
            return memo[(l, r, last)]

        # Try taking from the left
        if A[l] > last:
            # If the next player loses, then current player wins
            if not can_current_player_win(l + 1, r, A[l]):
                memo[(l, r, last)] = True
                return True

        # Try taking from the right
        if A[r] > last:
            # If the next player loses, then current player wins
            if not can_current_player_win(l, r - 1, A[r]):
                memo[(l, r, last)] = True
                return True

        # If no winning move found, current player loses
        memo[(l, r, last)] = False
        return False

    # Alice starts first
    if can_current_player_win(0, n - 1, -1):
        print("Alice")
    else:
        print("Bob")

if __name__ == "__main__":
    main()

