def is_valid(grid, n, m, k):
    # Create a copy of the grid to mark cells that are part of ticks
    # We'll use a 2D array to track whether a cell is part of a tick
    ticked = [[False for _ in range(m)] for _ in range(n)]

    # For each cell in the grid
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '*':
                # Check all possible d >= k for which (i, j) could be part of a tick
                # The center (x, y) must satisfy that (i, j) is on the diagonal of a tick
                # So we check for all possible centers (x, y) such that (i, j) is on the diagonal
                # and that the tick size d is at least k
                found = False
                # Check for possible centers that could have (i, j) on their top-left diagonal
                max_d = min(i, j, n - 1 - i, m - 1 - j)
                for d in range(k, max_d + 1):
                    x, y = i + d, j + d
                    if 0 <= x < n and 0 <= y < m:
                        valid = True
                        for h in range(d + 1):
                            if grid[x - h][y - h] != '*':
                                valid = False
                                break
                        if valid:
                            # Mark all cells of this tick as part of a tick
                            for h in range(d + 1):
                                ticked[x - h][y - h] = True
                            found = True
                            break
                # Check for possible centers that could have (i, j) on their top-right diagonal
                max_d = min(i, m - 1 - j, n - 1 - i, j)
                for d in range(k, max_d + 1):
                    x, y = i + d, j - d
                    if 0 <= x < n and 0 <= y < m:
                        valid = True
                        for h in range(d + 1):
                            if grid[x - h][y + h] != '*':
                                valid = False
                                break
                        if valid:
                            # Mark all cells of this tick as part of a tick
                            for h in range(d + 1):
                                ticked[x - h][y + h] = True
                            found = True
                            break
                if not found:
                    return False
    # After processing all cells, check if all '*' cells are marked as part of a tick
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '*' and not ticked[i][j]:
                return False
    return True

