def is_composite(n):
    if n < 4:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return True
    return False

def solve():
    t = int(input())
    for _ in range(t):
        n = int(input())
        a = list(map(int, input().split()))

        total_sum = sum(a)

        if is_composite(total_sum):
            print(n)
            print(' '.join(map(str, range(1, n + 1))))
        else:
            # Try removing each element to find a composite sum
            found = False
            for i in range(n):
                new_sum = total_sum - a[i]
                if is_composite(new_sum):
                    print(n - 1)
                    result_indices = []
                    for j in range(n):
                        if j != i:
                            result_indices.append(j + 1)
                    print(' '.join(map(str, result_indices)))
                    found = True
                    break

            # If still not found (shouldn't happen according to problem statement)
            if not found:
                # This shouldn't happen according to problem guarantees, but just in case
                # try removing two elements
                for i in range(n):
                    for j in range(i + 1, n):
                        new_sum = total_sum - a[i] - a[j]
                        if is_composite(new_sum):
                            print(n - 2)
                            result_indices = []
                            for k in range(n):
                                if k != i and k != j:
                                    result_indices.append(k + 1)
                            print(' '.join(map(str, result_indices)))
                            found = True
                            break
                    if found:
                        break

solve()

