import sys

def compare(x1, p1, x2, p2):
    # Calculate the total number of digits in each number
    digits1 = len(str(x1)) + p1
    digits2 = len(str(x2)) + p2

    # If number of digits differ, we can decide immediately
    if digits1 > digits2:
        return '>'
    elif digits1 < digits2:
        return '<'

    # If same number of digits, we need to compare the actual values
    # We'll normalize by making the powers equal

    if p1 > p2:
        # x1 * 10^(p1-p2) vs x2
        diff = p1 - p2
        # To avoid overflow, we can append zeros to x2 instead of multiplying x1
        # x1 vs x2 * 10^(p1-p2)
        if diff > 18:  # Beyond safe integer range for typical systems
            return '>'
        else:
            x2_adjusted = x2 * (10 ** diff)
            if x1 > x2_adjusted:
                return '>'
            elif x1 < x2_adjusted:
                return '<'
            else:
                return '='
    elif p2 > p1:
        # x1 vs x2 * 10^(p2-p1)
        diff = p2 - p1
        if diff > 18:  # Beyond safe integer range
            return '<'
        else:
            x1_adjusted = x1 * (10 ** diff)
            if x1_adjusted > x2:
                return '>'
            elif x1_adjusted < x2:
                return '<'
            else:
                return '='
    else:
        # p1 == p2, just compare x1 and x2
        if x1 > x2:
            return '>'
        elif x1 < x2:
            return '<'
        else:
            return '='

t = int(sys.stdin.readline())
