def find(parent, x):
    if parent[x] != x:
        parent[x] = find(parent, parent[x])
    return parent[x]

def union(parent, rank, x, y):
    px, py = find(parent, x), find(parent, y)
    if px == py:
        return
    if rank[px] < rank[py]:
        px, py = py, px
    parent[py] = px
    if rank[px] == rank[py]:
        rank[px] += 1

def solve(s):
    n = len(s)
    parent = list(range(n))
    rank = [0] * n

    # Process 'E' constraints to form connected components
    for i in range(n):
        if s[i] == 'E':
            union(parent, rank, i, (i + 1) % n)

    # Check for direct contradictions: if two adjacent positions 
    # are in the same component but constrained to be different
    for i in range(n):
        if s[i] == 'N':
            if find(parent, i) == find(parent, (i + 1) % n):
                return "NO"

    # Create a graph between components based on 'N' constraints
    component_set = set()
    for i in range(n):
        component_set.add(find(parent, i))

    component_list = list(component_set)
    comp_index = {comp: idx for idx, comp in enumerate(component_list)}
    num_components = len(component_list)

    # Build adjacency list for component graph
    adj = [[] for _ in range(num_components)]

    for i in range(n):
        if s[i] == 'N':
            comp1 = comp_index[find(parent, i)]
            comp2 = comp_index[find(parent, (i + 1) % n)]
            if comp1 != comp2:  # Only add edge if different components
                adj[comp1].append(comp2)
                adj[comp2].append(comp1)

    # Check if the component graph is bipartite
    color = [-1] * num_components

    for i in range(num_components):
        if color[i] == -1:
            queue = [i]
            color[i] = 0
            while queue:
                u = queue.pop(0)
                for v in adj[u]:
                    if color[v] == -1:
                        color[v] = 1 - color[u]
                        queue.append(v)
                    elif color[v] == color[u]:
                        return "NO"

    return "YES"

t = int(input())
for _ in range(t):
    s = input().strip()
    print(solve(s))

