def sieve_of_eratosthenes(limit):
    """Generate prime numbers up to limit using Sieve of Eratosthenes."""
    is_prime = [True] * (limit + 1)
    is_prime[0] = is_prime[1] = False
    for i in range(2, int(limit**0.5) + 1):
        if is_prime[i]:
            for j in range(i*i, limit + 1, i):
                is_prime[j] = False
    return [i for i in range(2, limit + 1) if is_prime[i]]

def prime_factors(n, primes):
    """Return the prime factorization of n as a dictionary {prime: exponent}."""
    factors = {}
    for p in primes:
        if p * p > n:
            break
        if n % p == 0:
            count = 0
            while n % p == 0:
                n //= p
                count += 1
            factors[p] = count
    if n > 1:
        factors[n] = 1
    return factors

def legendre_exponent(n, p):
    """Calculate the highest power of prime p that divides n! using Legendre's formula."""
    exp = 0
    power_of_p = p
    while power_of_p <= n:
        exp += n // power_of_p
        if power_of_p > n // p:  # Prevent overflow
            break
        power_of_p *= p
    return exp

def mod_exp(base, exp, mod):
    """Compute (base^exp) % mod using fast exponentiation."""
    result = 1
    base %= mod
    while exp > 0:
        if exp & 1:
            result = (result * base) % mod
        base = (base * base) % mod
        exp >>= 1
    return result

def solve(x, n):
    MOD = 1000000007

    # Generate primes up to sqrt(x) for efficient factorization
    primes = sieve_of_eratosthenes(int(x**0.5) + 1)

    # Get prime factorization of x
    factors = prime_factors(x, primes)

    result = 1
    for prime, _ in factors.items():
        # Calculate the total exponent of this prime in the product
        total_exp = legendre_exponent(n, prime)

        # Multiply result by (prime ^ total_exp) mod MOD
        result = (result * mod_exp(prime, total_exp, MOD)) % MOD

    return result

