                i += 1
    return lps

def find_longest_common_subsequence_without_virus(s1, s2, virus):
    n1, n2, nv = len(s1), len(s2), len(virus)

    # Handle edge cases
    if n1 == 0 or n2 == 0 or nv == 0:
        return "0"

    # If virus is longer than possible LCS, then any LCS is valid
    if nv > min(n1, n2):
        # Find regular LCS
        dp = [[0] * (n2 + 1) for _ in range(n1 + 1)]
        for i in range(1, n1 + 1):
            for j in range(1, n2 + 1):
                if s1[i-1] == s2[j-1]:
                    dp[i][j] = dp[i-1][j-1] + 1
                else:
                    dp[i][j] = max(dp[i-1][j], dp[i][j-1])

