            # Start with single digit numbers (avoid leading zero issue by starting with non-zero)
            if digit1 != 0:
                queue.append(digit1)
            if digit2 != 0 and digit2 != digit1:
                queue.append(digit2)

            # If both digits are same and non-zero, start with that digit
            if digit1 == digit2 and digit1 != 0:
                queue = deque([digit1])
            elif digit1 == digit2 and digit1 == 0:
                # Special case: only zeros - but we want positive integers, so skip
                queue = deque()

            while queue:
                current_num = queue.popleft()
                if current_num > n:
                    continue

                result_set.add(current_num)

                # Generate next numbers by appending either digit
