import sys

def is_valid_email(email):
    if not email:
        return False
    if email.startswith('.') or email.endswith('.') or \
       email.startswith('@') or email.endswith('@'):
        return False
    if email.count('@') != 1:
        return False

    parts = email.split('@')
    if len(parts) != 2 or not parts[0] or not parts[1]:
        return False

    # Check if all characters are valid
    for c in email:
        if not (c.islower() or c in '.@'):
            return False

    return True

def solve(s):
    n = len(s)
    # memo[i] stores all possible valid email endings starting from position i
    memo = {}

    def dp(pos):
        if pos in memo:
            return memo[pos]

        if pos == n:
            return [""]

        results = []

        # Try matching "at" -> "@"
        if pos + 1 < n and s[pos:pos+2] == "at":
            for rest in dp(pos + 2):
                results.append("@" + rest)

        # Try matching "dot" -> "."
        if pos + 2 < n and s[pos:pos+3] == "dot":
            for rest in dp(pos + 3):
                results.append("." + rest)

        # Take current character literally (only if it's a lowercase letter)
        if s[pos].islower():
            for rest in dp(pos + 1):
                results.append(s[pos] + rest)

        memo[pos] = results
        return results

    # Generate all possible interpretations
    all_possibilities = dp(0)

    # Filter valid emails
    valid_emails = []
    for possibility in all_possibilities:
        if is_valid_email(possibility):
            valid_emails.append(possibility)

    # Sort by length first, then lexicographically
    if valid_emails:
        valid_emails.sort(key=lambda x: (len(x), x))
        return valid_emails[0]

    return ""

# Read input and solve
input_str = input().strip()
print(solve(input_str))

