def psi(n):
    s = str(n)
    reflected = ''.join(str(9 - int(d)) for d in s)
    return int(reflected)

def weight(n):
    return n * psi(n)

def solve(l, r):
    # For small ranges, brute force
    if r - l <= 10**6:
        return max(weight(i) for i in range(l, r + 1))

    max_w = 0

    # Always check boundaries
    candidates = [l, r]

    # Generate strategic candidates
    str_l = str(l)
    str_r = str(r)
    len_r = len(str_r)

    # Check powers of 10 and their neighbors
    for length in range(len(str_l), len_r + 1):
        # Check 10^(length-1) if valid
        if length > 0:
            pow10 = 10 ** (length - 1)
            if l <= pow10 <= r:
                candidates.append(pow10)

            # Check pow10 - 1 (all 9s)
            if pow10 > 0:
                all_nines = pow10 - 1
                if l <= all_nines <= r:
                    candidates.append(all_nines)

        # Check 10^length if valid
        pow10_next = 10 ** length
        if l <= pow10_next <= r:
            candidates.append(pow10_next)

    # Add some additional strategic points
    # Check numbers like 4999..., 5000..., etc.
    for length in range(max(1, len(str_l)), len_r + 1):
        # Mid-range candidates
        mid_point = 5 * (10 ** (length - 1))
        if l <= mid_point <= r:
            candidates.append(mid_point)

        # Numbers with repeated digits
        for digit in [4, 5, 6]:
            rep_num = int(str(digit) * length)
            if l <= rep_num <= r:
                candidates.append(rep_num)

    # Remove duplicates and sort
    candidates = list(set(candidates))

    # Evaluate all candidates
    for c in candidates:
        if l <= c <= r:
            max_w = max(max_w, weight(c))

    return max_w

