    data = sys.stdin.read().strip().split()
    if not data:
        return
    n = int(data[0])

    bits = n.bit_length()               # number of binary digits
    res = []
    for i in range(bits - 1, -1, -1):    # from most significant to least
        if (n >> i) & 1:
