def solve():
    s = input().strip()
    target = "hello"
    target_index = 0

    # Iterate through each character in the input string
    for i in range(len(s)):
        # If we've matched all characters in "hello", break early
        if target_index == len(target):
            break

        # If current character matches what we're looking for, advance target_index
        if s[i] == target[target_index]:
            target_index += 1

    # Check if we matched all characters in "hello"
    if target_index == len(target):
        print("YES")
    else:
        print("NO")

# Call the function
solve()
