import sys
from collections import defaultdict

def get_divisors(n):
    divisors = set()
    i = 1
    while i * i <= n:
        if n % i == 0:
            divisors.add(i)
            divisors.add(n // i)
        i += 1
    return sorted(list(divisors))

def main():
    MOD = 10**9 + 7

    def modinv(x):
        return pow(x, MOD-2, MOD)

    n, k = map(int, sys.stdin.readline().split())

    # Get all divisors of n
    divs = get_divisors(n)
    div_idx = {d: i for i, d in enumerate(divs)}
    m = len(divs)

    # Precompute for each divisor, which of its divisors are also in the list
    children = [[] for _ in range(m)]
    for i, d in enumerate(divs):
        for dd in get_divisors(d):
            if dd in div_idx:
                children[i].append(div_idx[dd])

    # dp[i][j] = probability of being at divisor i after j steps
    # But we'll use expectation: E[i][j] = expected value after j steps starting from divisor divs[i]
    E = [0] * m
    # Initially (after 0 steps), we're at n, so E[div_idx[n]] = n, rest 0
    E[div_idx[n]] = n

    # Now do k steps
    for _ in range(k):
        new_E = [0] * m
        for i in range(m):
            d = divs[i]
            child_list = children[i]
            deg = len(child_list)
            if deg == 0:
                new_E[i] = 0
            else:
                inv_deg = modinv(deg)
                for j in child_list:
                    new_E[j] = (new_E[j] + E[i] * inv_deg) % MOD
        E = new_E

    print(E[div_idx[n]])

