def sieve(n):
    is_prime = [True] * (n + 1)
    is_prime[0] = is_prime[1] = False
    for i in range(2, int(n**0.5) + 1):
        if is_prime[i]:
            for j in range(i*i, n + 1, i):
                is_prime[j] = False
    return [i for i in range(2, n + 1) if is_prime[i]]

def prime_factorize(n, primes):
    factors = {}
    for p in primes:
        if p * p > n:
            break
        if n % p == 0:
            count = 0
            while n % p == 0:
                n //= p
                count += 1
            factors[p] = count
    if n > 1:
        factors[n] = 1
    return factors

def power_mod(base, exp, mod):
    result = 1
    base %= mod
    while exp > 0:
        if exp & 1:
            result = result * base % mod
        base = base * base % mod
        exp >>= 1
    return result

def modinv(a, mod):
    return power_mod(a, mod - 2, mod)

def solve(x, n):
    MOD = 10**9 + 7
    primes = sieve(int(x**0.5) + 1000)
    factors = prime_factorize(x, primes)

    result = 1
    for p, alpha in factors.items():
        # Compute contribution of prime p to the final answer
        # This is equivalent to computing p^(sum_{i=1}^n floor(n / p^k))
        # But we do it efficiently by considering how many numbers from 1 to n contribute p^j
        power_sum = 0
        pk = p
        while pk <= n:
            power_sum += n // pk
            if power_sum >= MOD - 1:
                power_sum %= (MOD - 1)
            pk *= p

        # Multiply result by p^power_sum mod MOD
        result = result * power_mod(p, power_sum, MOD) % MOD

    return result

