import math

def find_unlucky_years(x, y, l, r):
    unlucky = set()

    # Generate all possible values of x^a where a >= 0 and x^a <= r
    pow_x = [1]
    while pow_x[-1] <= r:
        if len(pow_x) > 1 and pow_x[-1] > r // x:
            break
        pow_x.append(pow_x[-1] * x)

    # Generate all possible values of y^b where b >= 0 and y^b <= r
    pow_y = [1]
    while pow_y[-1] <= r:
        if len(pow_y) > 1 and pow_y[-1] > r // y:
            break
        pow_y.append(pow_y[-1] * y)

    # Combine all possible sums x^a + y^b that fall within [l, r]
    for px in pow_x:
        for py in pow_y:
            sum_val = px + py
            if sum_val > r:
                break
            if sum_val >= l:
                unlucky.add(sum_val)

    # Convert to sorted list
    unlucky_years = sorted(unlucky)

    # If no unlucky years in range, the whole interval is golden
    if not unlucky_years:
        return r - l + 1

    max_gap = 0

    # Check gap before first unlucky year
    if unlucky_years[0] > l:
        max_gap = max(max_gap, unlucky_years[0] - l)

    # Check gaps between consecutive unlucky years
    for i in range(1, len(unlucky_years)):
        gap = unlucky_years[i] - unlucky_years[i-1] - 1
        if gap > 0:
            max_gap = max(max_gap, gap)

    # Check gap after last unlucky year
    if unlucky_years[-1] < r:
        max_gap = max(max_gap, r - unlucky_years[-1])

    return max_gap

