    # Compute the product of factorials of digits in a
    product = 1
    for digit in a:
        product *= fact[int(digit)]

    # Generate all possible numbers without 0 and 1 digits
    # and find the maximum one with the same product
    def backtrack(current_product, current_number):
        nonlocal max_number
        if current_product == product:
            if current_number > max_number:
