def remove_balls(balls):
    while True:
        n = len(balls)
        if n < 3:
            break
        i = 0
        changed = False
        while i < n:
            j = i
            while j < n and balls[j] == balls[i]:
                j += 1
            if j - i >= 3:
                balls = balls[:i] + balls[j:]
                changed = True
                break
            i = j
        if not changed:
            break
    return balls

def solve():
    n, k, x = map(int, input().split())
    balls = list(map(int, input().split()))

    max_destroyed = 0

    for i in range(n + 1):
        new_balls = balls[:i] + [x] + balls[i:]
        removed_balls = remove_balls(new_balls)
        destroyed = n + 1 - len(removed_balls)
        max_destroyed = max(max_destroyed, destroyed)

    print(max_destroyed)

solve()

def solve():
    a, k = input().split()
    k = int(k)
    digits = list(a)
    n = len(digits)

    for i in range(n):
        if k == 0:
            break
        max_digit = digits[i]
        max_index = i
        for j in range(i + 1, min(n, i + k + 1)):
            if digits[j] > max_digit:
                max_digit = digits[j]
                max_index = j
        k -= (max_index - i)
        for j in range(max_index, i, -1):
            digits[j], digits[j-1] = digits[j-1], digits[j]

    print(''.join(digits))

solve()
