def factorial(n):
    if n <= 1:
        return 1
    result = 1
    for i in range(2, n + 1):
        result *= i
    return result

def solve(A, B):
    # GCD(A!, B!) = min(A, B)!
    min_val = min(A, B)
    return factorial(min_val)

