def solve():
    n = int(input())
    s = input().strip()

    # Count consecutive x's and calculate minimum removals
    consecutive_x = 0
    removals = 0

    for i in range(n):
        if s[i] == 'x':
            consecutive_x += 1
        else:
            # End of consecutive x sequence
            if consecutive_x >= 3:
                removals += consecutive_x - 2
            consecutive_x = 0

    # Check the last sequence (in case string ends with x's)
    if consecutive_x >= 3:
        removals += consecutive_x - 2

    return removals

# Solve and print result
print(solve())
