def solve(n):
    if n % 2 == 0:
        return "Mahmoud"
    else:
        return "Ehab"

# Read input
n = int(input())

# Solve and print result
result = solve(n)
print(result)

def min_cuts(n):
    slices = n + 1

    if slices == 1:
        return 0
    elif slices % 2 == 0:
        return slices // 2
    else:
        return slices

# Read input
n = int(input())

# Calculate and print result
result = min_cuts(n)
print(result)
