def solve():
    # Read input
    n, k = map(int, input().split())
    s = input().strip()

    # Count frequency of each color
    color_count = {}
    for color in s:
        color_count[color] = color_count.get(color, 0) + 1

    # Check if any color appears more than k times
    for count in color_count.values():
        if count > k:
            return "NO"

    return "YES"

# Execute the solution
print(solve())

def solve():
    n = int(input())
    s = input().strip()

    # Count transitions from S to F (Seattle to San Francisco)
    seattle_to_sf = 0
    # Count transitions from F to S (San Francisco to Seattle)
    sf_to_seattle = 0

    # Check each consecutive pair of days
    for i in range(n - 1):
        if s[i] == 'S' and s[i + 1] == 'F':
            seattle_to_sf += 1
        elif s[i] == 'F' and s[i + 1] == 'S':
            sf_to_seattle += 1

    # Compare the counts
    if seattle_to_sf > sf_to_seattle:
        return "YES"
    else:
        return "NO"

# Call the function and print the result
print(solve())

def factorial(n):
    if n <= 1:
        return 1
    result = 1
    for i in range(2, n + 1):
        result *= i
    return result

def solve(A, B):
    # GCD(A!, B!) = min(A, B)!
    min_val = min(A, B)
    return factorial(min_val)

# Read input
A, B = map(int, input().split())

# Solve and print result
result = solve(A, B)
print(result)
