def solve():
    k = int(input())
    grid = []
    for _ in range(4):
        grid.append(input().strip())

    # Count panels for each timing
    timing_count = [0] * 10  # index 0 unused, 1-9 for timings

    for i in range(4):
        for j in range(4):
            if grid[i][j] != '.':
                timing = int(grid[i][j])
                timing_count[timing] += 1

    # Check if any timing requires more presses than possible with two hands
    max_presses = 2 * k
    for count in timing_count:
        if count > max_presses:
            print("NO")
            return

    print("YES")

solve()

