def solve():
    MOD = 1000000007

    def pow_mod(base, exp, mod):
        result = 1
        base %= mod
        while exp > 0:
            if exp & 1:
                result = (result * base) % mod
            base = (base * base) % mod
            exp >>= 1
        return result

    k, w = map(int, input().split())

    # If w >= k, there are infinitely many valid strings
    if w >= k:
        print(-1)
        return

    # For w < k, the answer is k * (k-1)^(w-1)
    if w == 1:
        result = k % MOD
    else:
        result = (k * pow_mod(k - 1, w - 1, MOD)) % MOD

    print(result)

solve()

