from collections import deque

def count_undoubtedly_lucky(n):
    lucky_numbers = set()

    # Try all pairs of digits (including same digit twice)
    for d1 in range(10):
        for d2 in range(d1, 10):  # d2 >= d1 to avoid duplicate pairs
            # Create set of allowed digits
            digits = {d1}
            if d1 != d2:
                digits.add(d2)

            # BFS to generate all numbers using only these digits
            queue = deque()

            # Add all non-zero starting digits
            for digit in digits:
                if digit != 0:
                    queue.append(str(digit))

            while queue:
                current = queue.popleft()
                num = int(current)

                if num <= n:
                    lucky_numbers.add(num)

                    # Extend if we can make longer numbers
                    if len(current) < len(str(n)):
                        for digit in digits:
                            new_num = current + str(digit)
                            if int(new_num) <= n:
                                queue.append(new_num)

    return len(lucky_numbers)

n = int(input())
print(count_undoubtedly_lucky(n))

