from itertools import product

def count_camels(n, t):
    count = 0

    # Generate all possible sequences of y-coordinates
    for seq in product(range(1, 5), repeat=n):
        # Check if no horizontal segments (no consecutive equal elements)
        valid = True
        for i in range(n-1):
            if seq[i] == seq[i+1]:
                valid = False
                break

        if not valid:
            continue

        # Count humps (local maxima) and valleys (local minima)
        humps = 0
        valleys = 0

        for j in range(1, n-1):
            if seq[j-1] < seq[j] > seq[j+1]:
                humps += 1
            elif seq[j-1] > seq[j] < seq[j+1]:
                valleys += 1

        # Check if exactly t humps and t-1 valleys
        if humps == t and valleys == t-1:
            count += 1

    return count

# Read input
n, t = map(int, input().split())

# Calculate and output result
result = count_camels(n, t)
print(result)

