import sys
from math import gcd
from functools import lru_cache

def lcm(a, b):
    return a * b // gcd(a, b)

@lru_cache(None)
def dp(pos, tight, started, remainder, mask, num_str):
    if pos == len(num_str):
        if not started:
            return 0

        # Calculate LCM of digits in mask
        if mask == 0:
            return 0

        digits_lcm = 1
        for i in range(9):
            if mask & (1 << i):
                digits_lcm = lcm(digits_lcm, i + 1)

        # Number is beautiful if remainder is 0 when divided by LCM of its digits
        return 1 if remainder % digits_lcm == 0 else 0

    limit = int(num_str[pos]) if tight else 9
    result = 0

    for digit in range(0, limit + 1):
        if not started and digit == 0:
            # Leading zero case
            result += dp(pos + 1, tight and (digit == limit), False, remainder, mask, num_str)
        else:
            # Non-leading zero case
            new_mask = mask
            if digit > 0:
                new_mask |= (1 << (digit - 1))

            new_remainder = (remainder * 10 + digit) % 2520
            result += dp(pos + 1, tight and (digit == limit), True, new_remainder, new_mask, num_str)

    return result

def count_beautiful(num):
    if num <= 0:
        return 0

    num_str = str(num)
    # Clear cache for new computation
    dp.cache_clear()

    return dp(0, True, False, 0, 0, num_str)

def solve(l, r):
    return count_beautiful(r) - count_beautiful(l - 1)

def main():
    t = int(input())
    for _ in range(t):
        l, r = map(int, input().split())
        print(solve(l, r))

if __name__ == "__main__":
    main()

