def solve(s):
    n = len(s)
    results = []

    def is_valid_email(email):
        if not email:
            return False
        # Check starts/ends with . or @
        if email[0] in ".@" or email[-1] in ".@":
            return False
        # Check exactly one @
        if email.count("@") != 1:
            return False
        # Check only valid characters
        for c in email:
            if not (c == "." or c == "@" or c.islower()):
                return False
        return True

    def backtrack(pos, current_result):
        if pos == n:
            if is_valid_email(current_result):
                results.append(current_result)
            return

        # Try to match "at" at current position
        if pos + 1 < n and s[pos:pos+2] == "at":
            backtrack(pos + 2, current_result + "@")

        # Try to match "dot" at current position
        if pos + 2 < n and s[pos:pos+3] == "dot":
            backtrack(pos + 3, current_result + ".")

        # Take current character as-is (if it's valid)
        if s[pos].islower() or s[pos] in ".@":
            backtrack(pos + 1, current_result + s[pos])

    backtrack(0, "")

    if not results:
        return ""

    # Find the best result (shortest, then lexicographically smallest)
    best = min(results, key=lambda x: (len(x), x))
    return best

# Read input and solve
input_str = input().strip()
print(solve(input_str))

