    n = int(input())
    s = input().strip()

    total_removals = 0
    consecutive_x = 0

    for i in range(n):
        if s[i] == 'x':
            consecutive_x += 1
        else:
            # End of consecutive x sequence
            if consecutive_x >= 3:
                total_removals += consecutive_x - 2
            consecutive_x = 0

    # Handle the case where string ends with x's
    if consecutive_x >= 3:
        total_removals += consecutive_x - 2

    return total_removals

