def get_divisors(n):
    divisors = []
    i = 1
    while i * i <= n:
        if n % i == 0:
            divisors.append(i)
            if i != n // i:
                divisors.append(n // i)
        i += 1
    return sorted(divisors)

def mod_inverse(a, mod):
    return pow(a, mod - 2, mod)

def solve(n, k):
    MOD = 1000000007

    # Get all divisors of n
    divisors = get_divisors(n)
    divisors_set = set(divisors)

    # Precompute divisors for each divisor
    div_map = {}
    for d in divisors:
        div_map[d] = [div for div in divisors if d % div == 0]

    # Initialize DP: probability distribution
    dp = {d: 0 for d in divisors}
    dp[n] = 1  # Start with n

    # Perform k steps
    for step in range(k):
        new_dp = {d: 0 for d in divisors}

        # For each current divisor, distribute its probability
        for d in divisors:
            if dp[d] > 0:
                divs_of_d = div_map[d]
                count = len(divs_of_d)
                prob_to_each = dp[d] * mod_inverse(count, MOD) % MOD

                for div in divs_of_d:
                    new_dp[div] = (new_dp[div] + prob_to_each) % MOD

        dp = new_dp

    # Calculate expected value
    expected = 0
    for d in divisors:
        expected = (expected + d * dp[d]) % MOD

    return expected

