from itertools import product

def solve():
    n, m = map(int, input().split())

    edges = []
    for _ in range(m):
        a, b = map(int, input().split())
        edges.append((a-1, b-1))  # Convert to 0-indexed

    if m == 0:
        return 0

    max_dominoes = 0

    # Try all possible assignments of values 1-6 to vertices
    for assignment in product(range(1, 7), repeat=n):
        # Count available dominoes
        domino_used = {}
        for i in range(1, 7):
            for j in range(i, 7):
                domino_used[(i, j)] = 0

        used_edges = 0

        # Try to assign dominoes to edges
        for u, v in edges:
            val_u = assignment[u]
            val_v = assignment[v]

            # Normalize the domino representation
            domino = (min(val_u, val_v), max(val_u, val_v))

            if domino_used[domino] < 1:
                domino_used[domino] += 1
                used_edges += 1

        max_dominoes = max(max_dominoes, used_edges)

    return max_dominoes

t = int(input())
for _ in range(t):
    print(solve())
