count = 0

# Iterate through all possible values of a and b
for a in range(1, n + 1):
    for b in range(a, n + 1):
        # Calculate c using XOR property: a ^ b ^ c = 0 means c = a ^ b
        c = a ^ b

        # Check if c is in valid range and maintains order a <= b <= c
        if c >= b and c <= n:
            # Check triangle inequality: a + b > c 
            if a + b > c:
                count += 1

