def solve():
    n = int(input())
    commands = input().strip()

    count = 0

    # Check all possible contiguous substrings
    for i in range(n):
        u_count = 0
        r_count = 0
        d_count = 0
        l_count = 0

        for j in range(i, n):
            # Update counts based on current command
            if commands[j] == 'U':
                u_count += 1
            elif commands[j] == 'R':
                r_count += 1
            elif commands[j] == 'D':
                d_count += 1
            elif commands[j] == 'L':
                l_count += 1

            # Check if we return to starting position
            if u_count == d_count and r_count == l_count:
                count += 1

    print(count)

solve()
