def count_triangles(w, h):
    total_points = (w + 1) * (h + 1)
    total_combinations = total_points * (total_points - 1) * (total_points - 2) // 6
    collinear_combinations = 0

    # Count collinear combinations along horizontal lines
    for y in range(h + 1):
        collinear_combinations += (w + 1) * (w + 2) * (w + 3) // 6

    # Count collinear combinations along vertical lines
    for x in range(w + 1):
        collinear_combinations += (h + 1) * (h + 2) * (h + 3) // 6

    # Count collinear combinations along diagonal lines from top-left to bottom-right
    for d in range(-(w + h), w + h + 1):
        if abs(d) > h and abs(d) > w:
            continue
        if d >= 0:
            length = min(w, h - d)
        else:
            length = min(h, w + d)
        collinear_combinations += length * (length + 1) * (length + 2) // 6

    # Count collinear combinations along diagonal lines from top-right to bottom-left
    for d in range(1, w + h + 1):
        if abs(d) > h and abs(d) > w:
            continue
        if d >= 0:
            length = min(h, w - d)
        else:
            length = min(w, h + d)
        collinear_combinations += length * (length + 1) * (length + 2) // 6

    total_non_degenerate_triangles = (total_combinations - collinear_combinations) % MOD
    return total_non_degenerate_triangles

